    @(posedge clk);
    RDATA_result  <= TRAN_s_axi_axilite_RDATA;
    RREADY_reg <= 0;
    if (TRAN_s_axi_axilite_RRESP === 2'b00 ) begin
        rresp <= 1;
        //output success. in fact RRESP is always 2'b00
    end  
    @(posedge clk);

//=======================one single read operate end======================

end
